import axios from 'axios';
import {create} from 'zustand';

const instance = axios.create({
   baseURL:'http://localhost:4000',
});


const store = create((set)=>({
   data: [],
   dataCtrl: function(n){
      
      instance.get("/")
      .then(res=>{
         set({data:res.data.list})
      })

      set((d)=>{
         console.log(d);
         return {data:d.data + 10}
      })
   }
}))

export default store;
