    dataCtrl({type:'post', data:{id:10, name:'rndnsrkawk'}});
  },[])

  return (
    <div className='todolist'>
      <h2>todos</h2>

      <div className='wrap'>
        <Insert/>
        <List/>
        <Sort/>
      </div>

    </div>
  );
}

export default App;
