import React from 'react'
import store from '../state/store'

const Sort = () => {
   let {sortCtrl} = store();
   function sortFn(type){
      sortCtrl({type});
   }

   return (
      <div className='footer'>
         <div>1 items</div>
         <div>
            <button onClick={()=>sortFn('All')}>All</button>
            <button onClick={()=>sortFn('Active')}>Active</button>
            <button onClick={()=>sortFn('Completed')}>Completed</button>
         </div>
      </div>
   )
}

export default Sort
