import React, {useEffect} from 'react';
import axios from 'axios';
import './App.css';
import store from './state/store';

function App() {

  const {data, dataCtrl} = store();
  console.log(data);

  // 1.get, delete (url, option)
  // 2.post, put (url, body, option)

  useEffect(()=>{
    dataCtrl({type:'post', data:{id:10, name:'rndnsrkawk'}});
  },[])

  return (
    <div className='todolist'>
      <h2>Todo</h2>
      <div>
        <form>
          <input type='text'/><button>+</button>
        </form>
      </div>

      <ul>
        <li>
          <p>
            <input type='checkbox'/>
            <label>리액트 공부하기</label>
          </p>
          <button>X</button>
        </li>
      </ul>
    </div>
  );
}

export default App;
