import React, {useEffect} from 'react';
import './App.css';
import store from './state/store';
import './asset/css/todos.scss';

function App() {

  const {data, dataCtrl} = store();
  console.log(data);

  // 1.get, delete (url, option)
  // 2.post, put (url, body, option)

  useEffect(()=>{
    dataCtrl({type:'post', data:{id:10, name:'rndnsrkawk'}});
  },[])

  return (
    <div className='todolist'>
      <h2>todos</h2>

      <div className='wrap'>
        <div className='insert'>
          <form>
            <input type='text' name='todo' placeholder='오늘은 무엇을 하실건가요?'/><button> + </button>
          </form>
        </div>

        <ul>
          <li>
            <p>
              <input id='check' type='checkbox'/>
              <label for='check'>리액트 공부하기</label>
            </p>
            <button> X </button>
          </li>
        </ul>

        <div className='footer'>
          <div>1 items</div>
          <div>
              <button>All</button>
              <button>Active</button>
              <button>Completed</button>
            </div>
        </div>
      </div>

    </div>
  );
}

export default App;
